export default {
    data() {
        return {
            websocket: null,
        };
    },
    methods: {
        initializeWebSocket(userId) {
            const websocket = new WebSocket(`ws://localhost:9090/imserverSingle/${userId}`);
            this.websocket.onopen = this.onWebSocketOpen;
        },
        onWebSocketOpen() {
            console.log('WebSocket连接已打开');
            // 连接成功后的逻辑，例如获取初始数据
            // 存储 WebSocket 连接到 Vuex
            this.$store.commit('setWebSocket', websocket);
        },
        onWebSocketClose() {
            console.log('WebSocket连接已关闭');
            // 连接关闭后的逻辑
            // 清理 Vuex 中的 WebSocket 连接
            this.$store.commit('clearWebSocket');
        },
        onWebSocketMessage(event) {
            console.log('收到WebSocket消息:', event.data);
            // 处理收到的消息
            // 处理收到的WebSocket消息
        },
        // 其他WebSocket相关方法和逻辑
    },
    // 在组件销毁前关闭WebSocket连接
    beforeDestroy() {
        const websocket = this.$store.state.websocket;
        if (websocket) {
            websocket.close();
        }
    },
};
